import React, { Component } from "react";
import { withRouter, Redirect } from "react-router-dom";
import { connect } from "react-redux";
import Auth from "../../Auth";
import TextField from "@material-ui/core/TextField";
import Button from "@material-ui/core/Button";
import { setLoggedInUser } from "../../Redux/Actions";
import Avatar from '@material-ui/core/Avatar';
import LockOutlinedIcon from '@material-ui/icons/LockOutlined';


class ConnectedLogin extends Component {
    constructor(props){
        super(props);
        var localloginComponent=[];
        localloginComponent.push(
            <MuiThemeProvider key={"theme"}>
                <div>
                    <TextField
                        hintText="Enter your e-posta address"
                        floatingLabelText="E-posta"
                        onChange={(event,newValue) => this.setState({username:newValue})}
                    />
                    <br/>
                    <TextField
                        type="password"
                        hintText="Enter your Password"
                        floatingLabelText="Password"
                        onChange = {(event,newValue) => this.setState({password:newValue})}
                    />
                    <br/>
                    <RaisedButton label="Login" primary={true} style={style} onClick={(event) => this.handleClick(event)}/>
                </div>
            </MuiThemeProvider>
        )
        this.state={
            username:'',
            password:'',
            menuValue:1,
            loginComponent:localloginComponent,
            loginRole:'customer'
        }
    }
    componentWillMount(){
        // console.log("willmount prop values",this.props);
        if(this.props.role != undefined){
            if(this.props.role == 'customer'){
                console.log("in customer componentWillMount");
                var localloginComponent=[];
                localloginComponent.push(
                    <MuiThemeProvider>
                        <div>
                            <TextField
                                hintText="Enter your e-posta address"
                                floatingLabelText="E-posta"
                                onChange = {(event,newValue) => this.setState({username:newValue})}
                            />
                            <br/>
                            <TextField
                                type="password"
                                hintText="Enter your Password"
                                floatingLabelText="Password"
                                onChange = {(event,newValue) => this.setState({password:newValue})}
                            />
                            <br/>
                            <RaisedButton label="Login" primary={true} style={style} onClick={(event) => this.handleClick(event)}/>
                        </div>
                    </MuiThemeProvider>
                )
                this.setState({menuValue:1,loginComponent:localloginComponent,loginRole:'customer'})
            }
            else if(this.props.role == 'product owner'){
                console.log("product owner componentWillMount");
                var localloginComponent=[];
                localloginComponent.push(
                    <MuiThemeProvider>
                        <div>
                            <TextField
                                hintText="Enter your e-posta address"
                                floatingLabelText="Product owner"
                                onChange={(event,newValue) => this.setState({username:newValue})}
                            />
                            <br/>
                            <TextField
                                type="password"
                                hintText="Enter your Password"
                                floatingLabelText="Password"
                                onChange={(event,newValue) => this.setState({password:newValue})}
                            />
                            <br/>
                            <RaisedButton label="Login" primary={true} style={style} onClick={(event) => this.handleClick(event)}/>
                        </div>
                    </MuiThemeProvider>
                )
                this.setState({menuValue:2,loginComponent:localloginComponent,loginRole:'product owner'})
            }
        }
    }
    handleClick(event){
        var self = this;
        var payload={
            "userid":this.state.username,
            "password":this.state.password,
            "role":this.state.loginRole
        }
        axios.post(apiBaseUrl+'login', payload)
            .then(function (response) {
                console.log(response);
                if(response.data.code == 200){
                    console.log("Login successfull");
                    var uploadScreen=[];
                    uploadScreen.push(<UploadPage appContext={self.props.appContext} role={self.state.loginRole}/>)
                    self.props.appContext.setState({loginPage:[],uploadScreen:uploadScreen})
                }
                else if(response.data.code == 204){
                    console.log("E-posta password do not match");
                    alert(response.data.success)
                }
                else{
                    console.log("E-posta does not exists");
                    alert("E-posta does not exist");
                }
            })
            .catch(function (error) {
                console.log(error);
            });
    }
    handleMenuChange(value){
        console.log("menuvalue",value);
        var loginRole;
        if(value==1){
            var localloginComponent=[];
            loginRole='customer';
            localloginComponent.push(
                <MuiThemeProvider>
                    <div>
                        <TextField
                            hintText="Enter your e-posta address"
                            floatingLabelText="E-posta"
                            onChange = {(event,newValue) => this.setState({username:newValue})}
                        />
                        <br/>
                        <TextField
                            type="password"
                            hintText="Enter your Password"
                            floatingLabelText="Password"
                            onChange = {(event,newValue) => this.setState({password:newValue})}
                        />
                        <br/>
                        <RaisedButton label="Login" primary={true} style={style} onClick={(event) => this.handleClick(event)}/>
                    </div>
                </MuiThemeProvider>
            )
        }
        else if(value == 2){
            var localloginComponent=[];
            loginRole='product owner';
            localloginComponent.push(
                <MuiThemeProvider>
                    <div>
                        <TextField
                            hintText="Enter your e-posta address"
                            floatingLabelText="E-posta"
                            onChange = {(event,newValue) => this.setState({username:newValue})}
                        />
                        <br/>
                        <TextField
                            type="password"
                            hintText="Enter your Password"
                            floatingLabelText="Password"
                            onChange = {(event,newValue) => this.setState({password:newValue})}
                        />
                        <br/>
                        <RaisedButton label="Login" primary={true} style={style} onClick={(event) => this.handleClick(event)}/>
                    </div>
                </MuiThemeProvider>
            )
        }
        this.setState({menuValue:value,
            loginComponent:localloginComponent,
            loginRole:loginRole})
    }
    render() {
        return (
            <div>
                <MuiThemeProvider>
                    <AppBar
                        title="Login"
                    />
                </MuiThemeProvider>
                <MuiThemeProvider>
                    <div>
                        <p>Login as:</p>
                        <DropDownMenu value={this.state.menuValue} onChange={(event,index,value)=>this.handleMenuChange(value)}>
                            <MenuItem value={1} primaryText="Customer" />
                            <MenuItem value={2} primaryText="Product owner" />
                        </DropDownMenu>
                    </div>
                </MuiThemeProvider>
                {this.state.loginComponent}
            </div>
        );
    }
}

const style = {
    margin: 15,
};

export default Login;
